import React, { Component } from 'react'
import './ShoppingCart.css'
import {cartData} from '../cartData';
class ShoppingCart extends Component {
   constructor(props) {
       super(props)
       this.res=localStorage.getItem('BooksIds');
       this.res=JSON.parse(this.res);

       this.state = {
         Qty:0,  
         datashop: cartData  

       }
   }

    // componentWillMount()
    // {    
    //     // this.res=localStorage.getItem('BooksIds');
    //     // this.res=JSON.parse(this.res);


    // }
    increaseQty = (id) => {



        // let modData = this.state.datashop.map(book => {
        //     if(book.id === id) book.qty++;
        //     return book;
        // });
        // this.setState(
        //     {
        //     datashop : modData          
        //     }
        //  );

        this.res.map((item)=>{
            if(item[0]===id){
                item[1]=item[1]+1;
            }
        })
        this.res=JSON.stringify(this.res);
        localStorage.setItem("BooksIds",this.res)


    }
    decreaseQty = (id,qty) => {

        if(qty===0)
        {
            alert ("can't decrease more");
        }
        else{
            let modData = this.state.datashop.map(book => {
                if(book.id === id) book.qty--;
                return book;
            });
            this.setState(
                {
                datashop : modData          
                }
             );


        }   
    }
    render() {

        return (


                <div className="cart-container"  >
                <div className="heading">
                    <h1>Shopping Cart</h1>
                    <div className="cart-card">

                    <table>
                    <tr>
                        <th className="cart-table"></th>

                        <th className="cart-table">Product Name</th>
                        <th className="cart-table">Price</th> 
                        <th className="cart-table">QTY</th>
                        <th className="cart-table">VAT</th>
                        <th className="cart-table">Total</th>
                    </tr>

                    { // add the local storage values only
                        this.res.map((items)=>
                         {
                          /************************************************ */   
                            console.log("res",items);

                           return (cartData.map((data1) => {
                              if(items[0]===data1.id)
                              { 
                            return  <tr key ={data1.id}>

                                <td className="cart-table"> <img className="cart-image" src="https://www.w3schools.com/w3images/avatar5.png" alt="kitten" /></td>
                                <td className="cart-table">{data1.name}</td>
                                <td className="cart-table">{data1.price}</td>
                                <td className="cart-table">
                                <button className="button-cart-qty" onClick={this.increaseQty.bind(this,data1.id)}>+</button>
                                    {items[1]}
                                <button className="button-cart-qty" onClick={this.decreaseQty.bind(this,data1.id,data1.qty)}>-</button>
                                </td>
                                <td className="cart-table">{data1.vat}</td>
                                <td className="cart-table">{data1.total}</td>
                            </tr>
                              }
                              return null}
                        )
                           )



                         /************************************************ */   
                            console.log("~~~~~~~~~this.state.dataShop",cartData[0]);



                         //********************************************** */   
                         } 

                        )

                        }

                    <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >Sub-Total</td>
                       <td >$999.9</td>

                    </tr>
                    <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >discount</td>
                       <td >$999.9</td>

                    </tr>
                    <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >Tax</td>
                       <td >$999.9</td>

                    </tr>
                    <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >Grand-Total</td>
                       <td >$999.9</td>

                    </tr>


                    </table>
                    </div>



            </div>
            </div>

        )
    }
}

export default ShoppingCart;
