import React, { Component } from 'react'
import './ShoppingCart.css'
import {cartData} from '../cartData';
class ShoppingCart extends Component {
   constructor(props) {
       super(props)

       this.state = {
         Qty:0,  
         datashop: cartData,  
         listOfBookid : []            
       }

   }

    componentWillMount()
    {
        let res=localStorage.getItem('BooksIds');
        this.state.data=JSON.parse(res);
        console.log("data in cart:=",this.state.data);
    }
    increaseQty = (id) => {

        let modData = this.state.datashop.map(book => {
            if(book.id === id) book.qty++;
            return book;
        });
        this.setState(
            {
            datashop : modData          
            }
         );

    }
    decreaseQty = (id,qty) => {

        if(qty===0)
        {
            alert ("can't decrease more");
        }
        else{
            let modData = this.state.datashop.map(book => {
                if(book.id === id) book.qty--;
                return book;
            });
            this.setState(
                {
                datashop : modData          
                }
             );
             let ret=localStorage.getItem("BooksIds");
             if(ret !== null)
             {   console.log("removing element");
                 let arr=JSON.parse(ret);
                 let index=arr.indexOf(id);
                 arr=arr.splice(index,id);
                 this.state.listOfBookid=[...arr];

                 // setting the local storage again
                 let jsonObjectOfBook=JSON.stringify(this.state.listOfBookid);
                 console.log(jsonObjectOfBook);
                 localStorage.setItem("BooksIds",jsonObjectOfBook);
             }



        }   
    }
    render() {

        return (


                <div className="cart-container"  >
                <div className="heading">
                    <h1>Shopping Cart</h1>
                    <div className="cart-card">

                    <table>
                    <tr>
                        <th className="cart-table"></th>

                        <th className="cart-table">Product Name</th>
                        <th className="cart-table">Price</th> 
                        <th className="cart-table">QTY</th>
                        <th className="cart-table">VAT</th>
                        <th className="cart-table">Total</th>
                    </tr>
                    { // add the local storage values only
                         this.state.datashop.map(
                             (data) =>

                                <tr key ={data.id}>
                                <td className="cart-table"> <img className="cart-image" src="https://www.w3schools.com/w3images/avatar5.png" alt="kitten" /></td>
                                <td className="cart-table">{data.name}</td>
                                <td className="cart-table">{data.price}</td>
                                <td className="cart-table">
                                <button className="button-cart-qty" onClick={this.increaseQty.bind(this,data.id,data.qty)}>+</button>
                                    {data.qty}
                                <button className="button-cart-qty" onClick={this.decreaseQty.bind(this,data.id,data.qty)}>-</button>
                                </td>
                                <td className="cart-table">{data.vat}</td>
                                <td className="cart-table">{data.total}</td>
                            </tr>
                         )
                     }       
                     <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >Sub-Total</td>
                       <td >$999.9</td>

                    </tr>
                    <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >discount</td>
                       <td >$999.9</td>

                    </tr>
                    <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >Tax</td>
                       <td >$999.9</td>

                    </tr>
                    <tr>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td ></td>
                       <td >Grand-Total</td>
                       <td >$999.9</td>

                    </tr>


                    </table>
                    </div>



            </div>
            </div>

        )
    }
}

export default ShoppingCart;
