import React, { Component } from 'react'
import './Search.css';

class Search extends Component {
    constructor(props){
        super(props);
        this.state={
            search:"Enter the book name"
        }

    }
    updateSearch=(e)=>
    {
        this.setState({
            search:e.target.value
        })
    }
    render() {
        return (
            <div className="Search-Bar">
            <input className="search-content" type ="text" value={this.state.search} onChange={this.updateSearch.bind(this)} name="search bar"></input>
            <button className="search-submit-button" type="submit" value="Submit" >Search</button>
         </div>
        )
    }
}

export default Search
