import React, { Component } from 'react'
import './Search.css';

class Search extends Component {
    constructor(props){
        super(props);
        this.state={
            search:""
        }

    }
    onChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    updateSearch=(e)=>
    {   console.log("in search.js",e.target.value);

        this.props.filterSearch(this.state.search );
    }
    render() {
        return (
            <div className="Search-Bar">
            <input className="search-content" type ="text" value={this.state.search} onChange={this.updateSearch.bind(this)} name="search bar"></input>
            <button className="search-submit-button" type="submit" value="Submit" >Search Book</button>
         </div>
        )
    }
}

export default Search
