import React, { Component } from 'react'
import './Card.css';
import {books} from '../data';

class Card extends Component {
    constructor(){
        super();
        this.state=
        {
        listOfBookId:[]
        }
// Retrive book details from api for home page
    }


    addToCartHandler = (id) => {
        console.log('clicked on the button');
        this.setState({
            listOfBookId:[ 
                id,
                ...this.state.listOfBookId
            ]
            });    
    }
    componentDidUpdate()
    {
        console.log(this.state.listOfBookId);
        this.state.listOfBookId.json();
        console.log(this.state.listOfBookId);

    }

    render() {
        return (
              <div className="container">

                  {books.map((Data) =>{
                      return <div className="card">
                        <img className="card-img" src="https://www.w3schools.com/w3images/avatar5.png" alt="Denim Jeans " />
                        <h1>{Data.name}</h1>
                        <p class="price">{Data.id}</p>
                        <p>Some text about the jeans. Super slim and comfy lorem ipsum lorem jeansum. Lorem jeamsun denim lorem jeansum.</p>
                        <p><button onClick={this.addToCartHandler.bind(this,Data.id)}>Add to Cart</button></p>
                        </div>

                  } )}

            </div>

        )
    }
}

export default Card;
